    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Promotion index{% endblock %}

{% block body %}
    <h1>Promotion index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Discount</th>
                <th>Code</th>
                <th>Startdate</th>
                <th>Enddate</th>
                <th>IsCoupon</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for promotion in promotions %}
            <tr>
                <td>{{ promotion.id }}</td>
                <td>{{ promotion.discount }}</td>
                <td>{{ promotion.code }}</td>
                <td>{{ promotion.startdate ? promotion.startdate|date('Y-m-d') : '' }}</td>
                <td>{{ promotion.enddate ? promotion.enddate|date('Y-m-d') : '' }}</td>
                <td>{{ promotion.isCoupon ? 'Yes' : 'No' }}</td>
                <td>
                    <a href=\"{{ path('crud_show', {'id': promotion.id}) }}\">show</a>
                    <a href=\"{{ path('crud_edit', {'id': promotion.id}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"7\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('crud_new') }}\">Create new</a>
{% endblock %}
", "crud/index.html.twig", "/home/ons/test/templates/crud/index.html.twig");
